    def __init__(self, *args):
        self.args = args

    def serialize(self):
        return json.dumps({'args':self.args})

class Point2D(Serializable):
    def __init__(self, x, y):
        super().__init__(x, y)
        self.x = x
        self.y = y

    def __repr__(self):
